import React from 'react';
import { TodoForm } from '../todoForm';
import {
  Route,
  Link
} from 'react-router-dom';
import { EditTodo } from '../editTodo';
import { Modal } from '../modal/Modal';

const todoList = [
  {id: 1, name: 'test1', title: 'rrrrrrrr'},
  {id: 2, name: 'test2', title: 'rrrrrrrr'},
  {id: 3, name: 'test3', title: 'rrrrrrrr'},
]

export class MainScreen extends React.Component {
  state = {
    todos: [...todoList],
    modal: true,
  }

  addTodo = (value) => {
    const { todos } = this.state;

    if(value) {
      this.setState(prev => ({
        todos: [
          ...prev.todos,
          {
            id: todos[todos.length - 1].id + 1,
            name: value,
            title: 'rrrrrrrr',
          }
        ]
      }))
    }

    return;
  }

  deleteTodos = (event) => {
    const targetId = +event.target.value

    // this.setState(prev =>({
    //   todos: prev.todos.filter(todo => todo.id !== targetId)
    // }))

    this.setState(prev =>({
      modal: !prev.modal
    }))

  }

  modalX = (x) => {
    this.setState({
      modal: x,
    })

    console.log(x)
  }

  render() {
    const { todos, modal } = this.state;
    console.log(todos)

    return (
      <>      
        <Route>
          <h1>TODO</h1>
        </Route>
        <Route path='/listTodos'>
          <TodoForm addTodo={this.addTodo}/>
          {
           todos.map(todo => (
              <>
                <div>{todo.name}</div>
                <button>
                  <Link to={`/${todo.name}`}>
                    edit Todo
                  </Link>
                </button>
                <button
                  value={todo.id}
                  onClick={(event) => (
                    this.deleteTodos(event)
                  )}
                >
                  delete Todo
                </button>
              </>
            ))
          }
        </Route>
        {
          todos.map(todo => (
            <Route path={`/${todo.name}`}>
              <h2>{todo.name}</h2>
              <EditTodo todo={todo}/>
            </Route>
          ))
        }
        <Modal modal={modal} modalX={this.modalX} />
      </>
    )
  }
}
