import React from 'react';
import { TodoForm } from '../todoForm';
import { Route, Link } from 'react-router-dom';
import { EditTodo } from '../editTodo';
import { Modal } from '../modal/Modal';

const todoList = [
  {id: 1, name: 'test1'},
  {id: 2, name: 'test2'},
  {id: 3, name: 'test3'},
]

export class MainScreen extends React.Component {
  state = {
    todos: [...todoList],
    modal: true,
    targetId: '',

  }

  addTodo = (value) => {
    const { todos } = this.state;

    if(value) {
      this.setState(prev => ({
        todos: [
          ...prev.todos,
          {
            id: todos[todos.length - 1].id + 1,
            name: value,
          }
        ]
      }))
    }

    return;
  }

  deleteTodos = (event) => {

    this.setState({
      targetId: event.target.value
    })

    this.setState(prev =>({
      modal: !prev.modal
    }))

  }

  confirmation = (decision) => {
    const { targetId } = this.state;

    this.setState(prev =>({
      modal: !prev.modal
    }))

    if (decision) {
      this.setState(prev =>({
        todos: prev.todos.filter(todo => todo.id !== +targetId)
      }))
    }

  }

  changeTodoName = (name, id) => {
    this.setState(prev =>({
      todos: prev.todos.map(todo => {
        if (todo.id === id) {
          todo.name = name
        }

        return todo
      })
    }))
  }

  render() {
    const { todos, modal } = this.state;

    return (
      <>      
        <Route>
          <h1>TODO</h1>
        </Route>
        <Route path='/listTodos'>
          <TodoForm addTodo={this.addTodo}/>
          {
           todos.map(todo => (
              <>
                <div>{todo.name}</div>
                <button>
                  <Link to={`/${todo.name}`}>
                    edit Todo
                  </Link>
                </button>
                <button
                  value={todo.id}
                  onClick={(event) => (
                    this.deleteTodos(event)
                  )}
                >
                  delete Todo
                </button>
              </>
            ))
          }
        </Route>
        {
          todos.map(todo => (
            <Route path={`/${todo.name}`}>
              <div>Old Todo: - {todo.name}</div>
              <EditTodo todo={todo} changeTodoName={this.changeTodoName} />
            </Route>
          ))
        }
        <Modal modal={modal} confirmation={this.confirmation} />
      </>
    )
  }
}
