    return (
      <div className={(modal) ? '' : 'modal'}>
        <div className='modal__content' hidden={modal}>
          <p hidden={modal}>{(modalEdd) ? 'save changes' : (modalCancel) ? 'cancel changes' : (modalMain) ? 'xxx' : '111'}</p>
          <>
            {
              (modalCancel) ? <div>1111</div> : <div>22222</div>
            }
          </>
        </div>
      </div>
    )
  }
}

