import React from 'react';

export class EditTodo extends React.Component {
  state = {
    name: this.props.todo.name,
    id: this.props.todo.id,
    nameSave: this.props.todo.name,
    idSave: '',
  }

  render() {
    const { name, id, nameSave } = this.state;
    const { changeTodoName } = this.props;
    console.log(nameSave)

    return (
      <>
      <div>New name: - {name}</div>
      <div>
        <input
          placeholder={name}
          onChange={(event) => {
            this.setState({
              name: event.target.value,
            })
          }} 
        >edit Todo</input>
        <button onClick={() => (
          changeTodoName(name, id)
        )} >
          save changes
        </button>
        <button onClick={() => (
          this.setState({
            name: nameSave,
          })
        )}>
          delete changes
        </button>
      </div>
      </>

    )
  }
}


