import React from 'react';
import { TodoForm } from '../todoForm';
import { Route, Link } from 'react-router-dom';
import { EditTodo } from '../editTodo';
import { Modal } from '../modal/Modal';
import DeleteIcon from '@material-ui/icons/Delete';
import IconButton from '@material-ui/core/IconButton';
import EditIcon from '@material-ui/icons/Edit';
import ListItem from '@material-ui/core/ListItem';
import Tooltip from '@material-ui/core/Tooltip';
import './Main.css';

const todoList = [
  {id: 1, name: 'test1'},
  {id: 2, name: 'test2'},
  {id: 3, name: 'test3'},
]


export class Main extends React.Component {
  state = {
    todos: [
      ...todoList
    ],
    modal: true,
    targetId: '',
    modalMain: true,

  }

  addTodo = (value) => {
    const { todos } = this.state;

    if(value) {
      this.setState(prev => ({
        todos: [
          ...prev.todos,
          {
            id: todos[todos.length - 1].id + 1,
            name: value,
          }
        ]
      }))
    }

    return;
  }

  deleteTodos = (event) => {

    this.setState({
      targetId: event.target.value
    })

    this.setState(prev =>({
      modal: !prev.modal
    }))

  }

  confirmation = (decision) => {
    const { targetId } = this.state;
    console.log(targetId)

    this.setState(prev =>({
      modal: !prev.modal
    }))

    if (decision) {
      this.setState(prev =>({
        todos: prev.todos.filter(todo => todo.id !== +targetId)
      }))
    }

  }

  changeTodoName = (name, id) => {
    localStorage.setItem('name', name)
    localStorage.setItem('id', id)
    this.setState(prev =>({
      todos: prev.todos.map(todo => {
        if (todo.id === id) {
          todo.name = name
        }

        return todo
      })
    }))
  }

  componentDidMount() {
    this.setState({
      todos: this.state.todos.map(todo => {
        if (todo.id === +localStorage.getItem('id')) {
          todo.name = localStorage.getItem('name')
        }

        return todo
      })
    })
  }

  render() {
    const { todos, modal, modalMain } = this.state;
    console.log(this.state.targetId)

    return (
      <div className='main'>      
        <Route>
          <Link to='/listTodos'>
            <h1>TODO - List</h1>
          </Link>
        </Route>
        <Route path='/listTodos'>
          <TodoForm addTodo={this.addTodo}/>
          {
           todos.map(todo => (
              <ListItem key={todo.id} dense button>
                <div>{todo.name}</div>
                <IconButton>
                  <Link to={`/${todo.name}`}>
                    <EditIcon />
                    Edit
                  </Link>
                </IconButton>
                <IconButton
                  value={todo.id}
                  onClick={(event) => (
                    this.deleteTodos(event)
                  )}
                >
                  <DeleteIcon/>
                  Delete
                </IconButton>
              </ListItem >
            ))
          }
        </Route>
        {
          todos.map(todo => (
            <Route path={`/${todo.name}`}>
              <div>Old Todo: - {todo.name}</div>
              <EditTodo todo={todo} changeTodoName={this.changeTodoName} />
            </Route>
          ))
        }
        <Modal modal={modal} confirmation={this.confirmation} modalMain={modalMain} />
      </div>
    )
  }
}
