import React from 'react';
import TextField from '@material-ui/core/TextField';
import { Modal } from '../modal/Modal';
import './EditTodo.css';


export class EditTodo extends React.Component {
  state = {
    name: this.props.todo.name,
    id: this.props.todo.id,
    nameSave: this.props.todo.name,
    modal: true,
    modalEdd: true,
    modalCancel: true,
  }

  editTodo = () => {
    this.setState(prev => ({
      modal: !prev.modal,
    }))
  }

  changesEditTodo = () => {
    this.setState(prev => ({
      modal: !prev.modal,
    }))
  }

  confirmation = (decision) => {
    const { name, id } = this.state;
    const { changeTodoName } = this.props;

    this.setState(prev =>({
      modal: !prev.modal
    }))

    if (decision) {
      changeTodoName(name, id)
    }

  }

  confirmationCancel = () => {
    this.setState({
      name: this.state.nameSave,
    })
  }

  render() {
    const { name, modal, modalEdd } = this.state;

    return (
      <div>
        <div>New Todo: - {name}</div>
        <div className='container'>
          <TextField
            className='container__child'
            id="outlined-basic"
            label="Enter New Todo"
            variant="outlined"
            placeholder='enter new todo'
            value={name}
            onChange={(event) => {
              this.setState({
               name: event.target.value,
               })
            }} 
          />
          <button className='container__child' onClick={this.editTodo}>
           save changes
          </button>
          <button className='container__child' onClick={this.confirmationCancel}>
            delete changes
          </button>
          </div>
        <Modal modal={modal} confirmation={this.confirmation} modalEdd={modalEdd}/>
      </div>

    )
  }
}
