import React from 'react';
import { TodoForm } from '../todoForm';
import {
  Route,
  Link
} from 'react-router-dom';

const todoList = [
  {id: 1, name: 'test1', title: 'rrrrrrrr'},
  {id: 2, name: 'test2', title: 'rrrrrrrr'},
  {id: 3, name: 'test3', title: 'rrrrrrrr'},
]

export class MainScreen extends React.Component {
  state = {
    todos: [...todoList],
  }

  addTodo = (value) => {
    const { todos } = this.state;

    this.setState(prev => ({
      todos: [
        ...prev.todos,
        {
          id: todos.length + 1,
          name: value,
          title: 'rrrrrrrr',
        }
      ]
    }))
  }

  render() {
    const { todos } = this.state;

    return (
      <>      
        <Route>
          <h1>TODO</h1>
        </Route>
        <Route path='/listTodos'>
          <TodoForm addTodo={this.addTodo}/>
          {
           todos.map(todo => (
              <>
                <div>{todo.name}</div>
                <button>
                  <Link to={`/${todo.name}`}>
                    edit Todo
                  </Link>
                </button>
                <button>delete Todo</button>
              </>
            ))
          }
        </Route>
        {
          todos.map(todo => (
            <Route path={`/${todo.name}`}>
              <h2>{todo.name}</h2>
            </Route>
          ))
        }
      </>
    )
  }
}
