import React from 'react';
import './Modal.css';

export class Modal extends React.Component {


  render() {
    const { modal } = this.props

    return (
      <div className={(modal) ? '' : 'modal'}>
        <div className='modal__content' hidden={modal}>
            <button onClick={() => (
              modal = !modal
            )}>X</button>
        </div>
      </div>
    )
  }
}
