import React from 'react';
import TextField from '@material-ui/core/TextField';
import { Modal } from '../modal/Modal';
import { Route } from 'react-router';

export class EditTodo extends React.Component {
  state = {
    name: this.props.todo.name,
    id: this.props.todo.id,
    nameSave: this.props.todo.name,
    idSave: '',
    modal: true,
  }

  editTodo = () => {
    this.setState(prev => ({
      modal: !prev.modal,
    }))
  }

  confirmation = (decision) => {
    const { name, id } = this.state;
    const { changeTodoName } = this.props;

    this.setState(prev =>({
      modal: !prev.modal
    }))

    if (decision) {
      changeTodoName(name, id)
    }

  }

  render() {
    const { name, nameSave, modal, decision } = this.state;

    return (
      <Route path={(decision) ? '' : `/${nameSave}`}>
        <div>New Todo: - {name}</div>
        <div>
          <TextField
            id="outlined-basic"
            label="New Todo"
            variant="outlined"
            placeholder='enter new todo'
            onChange={(event) => {
              this.setState({
               name: event.target.value,
               })
            }} 
          />
        <button onClick={this.editTodo}>
          save changes
        </button>
        <button onClick={() => (
          this.setState({
            name: nameSave,
          })
        )}>
          delete changes
        </button>
        </div>
        <Modal modal={modal} confirmation={this.confirmation}/>
      </Route>

    )
  }
}
