import React from 'react';
import { TodoForm } from '../todoForm';

const todoList = [
  {id: 1, name: 'test1'},
  {id: 2, name: 'test2'},
  {id: 3, name: 'test3'},
]

export class MainScreen extends React.Component {
  state = {
    todo: [...todoList],
  }

  render() {
    const { todo } = this.state;

    return (
      <>      
        <h1>TODO</h1>
        <TodoForm todo={todo}/>
        {
          todo.map(todo => (
            <>
              <div>{todo.name}</div>
              <button>delete</button>
              <button>edit Todo</button>
            </>
          ))
        }
      </>
    )
  }
}
