import React from 'react';
import TextField from '@material-ui/core/TextField';
import { Modal } from '../modal/Modal';


export class EditTodo extends React.Component {
  state = {
    name: this.props.todo.name,
    id: this.props.todo.id,
    nameSave: this.props.todo.name,
    modal: true,
    modalEdd: true,
    modalCancel: true,
  }

  editTodo = () => {
    this.setState(prev => ({
      modal: !prev.modal,
    }))
  }

  changesEditTodo = () => {
    this.setState(prev => ({
      modal: !prev.modal,
    }))
  }

  confirmation = (decision) => {
    const { name, id } = this.state;
    const { changeTodoName } = this.props;

    this.setState(prev =>({
      modal: !prev.modal
    }))

    if (decision) {
      changeTodoName(name, id)
    }

  }

  confirmationCancel = (decision) => {

    if (decision) {
      this.setState({
        name: this.state.nameSave,
      })
    }

  }

  render() {
    const { name, modal, modalEdd, modalCancel, nameSave } = this.state;

    return (
      <div>
        <div>New Todo: - {name}</div>
        <div>
          <TextField
            id="outlined-basic"
            label="New Todo"
            variant="outlined"
            placeholder='enter new todo'
            onChange={(event) => {
              this.setState({
               name: event.target.value,
               })
            }} 
          />
        <button onClick={this.editTodo}>
          save changes
        </button>
        <button>
          delete changes
        </button>
        </div>
        <Modal modal={modal} confirmation={this.confirmation} modalEdd={modalEdd}/>
      </div>

    )
  }
}
