import React from 'react';
import { Link } from 'react-router-dom';
import './Modal.css';

export class Modal extends React.Component {


  render() {
    const { modal, confirmation, modalEdd, modalCancel } = this.props

    return (
      <div className={(modal) ? '' : 'modal'}>
        <div className='modal__content' hidden={modal}>
        <p hidden={modal}>{(modalEdd) ? 'save changes' : (modalCancel) ? 'cancel changes' : 'delete todo'}</p>
            <Link to={(modalEdd) ? '/listTodos' : ''}>
              <button onClick={() => (
                confirmation(true)
              )}>
                Yes
              </button>
            </Link>
            <button onClick={() => (
              confirmation(false)
            )}>
              No
            </button>
        </div>
      </div>
    )
  }
}
