import React from 'react';
import TextField from '@material-ui/core/TextField';
import { Modal } from '../modal/Modal';

export class EditTodo extends React.Component {
  state = {
    name: this.props.todo.name,
    id: this.props.todo.id,
    nameSave: this.props.todo.name,
    idSave: '',
    modal: true,
  }

  editTodo = () => {
    const { name, id } = this.state;
    const { changeTodoName } = this.props;
    this.setState(prev => ({
      modal: !prev.modal,
    }))
    changeTodoName(name, id)
  }

  render() {
    const { name, nameSave, modal } = this.state;


    return (
      <>
        <div>New Todo: - {name}</div>
        <div>
          <TextField
            id="outlined-basic"
            label="New Todo"
            variant="outlined"
            placeholder='enter new todo'
            onChange={(event) => {
              this.setState({
               name: event.target.value,
               })
            }} 
          />
        <button onClick={this.editTodo}>
          save changes
        </button>
        <button onClick={() => (
          this.setState({
            name: nameSave,
          })
        )}>
          delete changes
        </button>
        </div>
        <Modal modal={modal} />
      </>

    )
  }
}
